package mathclasstype;

public class MathClassTrigonometric {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("90度的正弦值为：" + Math.sin(Math.PI / 2));
		System.out.println("0度的余弦值为：" + Math.cos(0));
		System.out.println("60度的正切值为：" + Math.tan(Math.PI / 3));
		System.out.println("取2的平方根与2的商的反正弦值：" + Math.asin(Math.sqrt(2) / 2));
		System.out.println("取2的平方根与2的商的反余弦值：" + Math.acos(Math.sqrt(2) / 2));
		System.out.println("取1的反正切值：" + Math.atan(1));
		System.out.println("120度的弧度值：" + Math.toRadians(120.0));
		System.out.println("π/2的角度值：" + Math.toDegrees(Math.PI / 2));
	}

}
